import React, {useState} from 'react';

function ToDoForm() {
const [input, setInput] = useState('');

    return (
        <form className='ToDoForm'>
            <input
                type='text' 
                placeholder='Add a todo' 
                value={input}
                name='text'
                className='todo-input'
            />
            <button className='todo-button'>Add
            todo</button>
        </form>
    );
}

export default ToDoForm;
