import React, {
    useState
} from 'react';
import ToDoForm from './ToDoForm';
import ToDo from './ToDo';



const ToDoList = (_props) => {
const [todos, setTodos] = useState([]) //storing input to state


const addTodo = todo => {
        if (!todo.text || /^\s*$/.test(todo.text)) { //Validating input is not blank
            return;
        }
        
        const newTodos = [todo, ...todos]; //creating an empty array for newtodos
        setTodos(newTodos);
    };



const updateToDo = (todoId, newValue) => {                      //to update a todo, we need the id of the todo and its current value
    if(!todos.text || /^\s*$/.test(todos.text)) {               //Validating for notblank
        return;

    }
    
    setTodos(prev => prev.map(item => (item.id === todoId ? newValue : item))       //
    );
};



const removeTodo = id => {
    const removeArr = [...todos].filter(todo => todo.id !== id);

        setTodos(removeArr);

    };

    
        console.log(todos)
    


const completeTodo = id => {
    let updatedTodos = ToDo.map(todo => {
        if (todo.id === id) {
            todo.isComplete = !todo.isComplete;
        }
        return todo
    });
    setTodos(updatedTodos);
};



return ( 
        <div>
            <h1> What's the Plan for Today?</h1>
            <ToDoForm onSubmit= {addTodo}/> 
            <ToDo todos={todos} completeTodo={completeTodo} removeTodo=
            {removeTodo} updateTodo
              />    
        </div>
    );
    
    };
export default ToDoList;
